import sysfrom PyQt4 import QtGui, QtCore, uicimport mathqtCreatorFile = '/home/jana/Genotipi/Genotipi_CODES/GenoMergeBox.ui' # Enter file here.Ui_MainWindow, QtBaseClass = uic.loadUiType(qtCreatorFile)class MergeDialog(QtGui.QMainWindow, Ui_MainWindow):    def __init__(self):        QtGui.QMainWindow.__init__(self)        Ui_MainWindow.__init__(self)        self.setupUi(self)        self.Breed.addItems(['-','Rjava', 'Holstein', 'Lisasta'])        self.AlleleFormat.addItems(['-','top/forward', 'ab', 'forward/reverse'])        self.NoSNPs.addItems(['-', '100', '200', '800'])        self.ZipFile.clicked.connect(self.choose_file)        self.tempDir.clicked.connect(self.choose_dir)        self.ZipDir.clicked.connect(self.choose_dir)        self.MergedDir.clicked.connect(self.choose_dir)        self.PeddarowDir.clicked.connect(self.choose_dir)        self.ZanardiDir.clicked.connect(self.choose_dir)        self.mergeYN = self.mergeAsk.isChecked()    def choose_dir(self):        DirPath = QtGui.QFileDialog.getExistingDirectory(self, 'Save Directory')        if DirPath:            self.ZipDirShow.setText(DirPath)    def choose_file(self):        FilePath = QtGui.QFileDialog.getOpenFileName(self, 'Choose File')        if FilePath:            self.ZipFileShow.setText(FilePath)if __name__ == "__main__":    app = QtGui.QApplication(sys.argv)    window = MergeDialog()    window.show()    sys.exit(app.exec_())